import React from 'react';
import { View, Text, TextInput, TouchableOpacity, Image } from 'react-native';

// Import your custom image assets
const menuIcon = require('../assets');
const filterIcon = require('./path-to-filter-icon.png');
const searchIcon = require('./path-to-search-icon.png');

const Homepage = () => {
  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.iconButton}>
        <Image source={menuIcon} style={styles.iconImage} />
      </TouchableOpacity>

      <View style={styles.searchContainer}>
        <View style={styles.searchBar}>
          <TextInput
            style={styles.input}
            placeholder="What would you like to eat?"
            placeholderTextColor="gray"
          />
          <Image source={searchIcon} style={styles.searchIcon} />
        </View>
      </View>

      <TouchableOpacity style={styles.iconButton}>
        <Image source={filterIcon} style={styles.iconImage} />
      </TouchableOpacity>
    </View>
  );
};

const styles = {
  container: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'white',
    paddingHorizontal: 10,
    borderRadius: 20,
    borderWidth: 1,
    borderColor: 'turquoise', // Turquoise border color
    marginHorizontal: 10,
    marginTop: 10,
  },
  iconButton: {
    padding: 10,
  },
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    flex: 1,
  },
  searchBar: {
    flexDirection: 'row',
    alignItems: 'center',
    flex: 1,
  },
  searchIcon: {
    width: 24,
    height: 24,
    marginLeft: 10,
  },
  iconImage: {
    width: 24,
    height: 24,
  },
  input: {
    flex: 1,
    color: 'gray', // Placeholder text color
  },
};

export default Homepage;
